def write_df(df_spark,
             save_path,
             save_format="parquet",
             save_mode="overwrite"):
    df_spark.write.format(save_format).mode(save_mode).save(save_path)


def read_df(spark, read_path, save_format="parquet"):
    return spark.read.format(save_format).load(read_path)


def write_pipeline(pipeline_model, save_path):
    pipeline_model.write().overwrite().save(save_path)
