from enum import Enum, auto


class AutoName(Enum):

    def _generate_next_value_(name, start, count, last_values):
        return name.lower()


class Operations(AutoName):
    fetch = auto()
    clean = auto()
    feature_extract = auto()
    label = auto()
    model_train = auto()
    model_test = auto()
    produce_tweet = auto()
    stream_predict = auto()
